contracts/AgentVault.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

import "@openzeppelin/contracts/token/ERC20/ERC20.sol";
import "@openzeppelin/contracts/token/ERC20/extensions/ERC4626.sol";
import "@openzeppelin/contracts/utils/ReentrancyGuard.sol";
import "@openzeppelin/contracts/access/AccessControl.sol";
import "./interface/IStrategy.sol";
import "./interface/IAgentVault.sol";

/**
 * @title AgentVault
 * @notice Implementation of IAgentVault interface
 * - Users can deposit/withdraw assets
 * - On deposit, Vault calls Strategy.beforeDeposit() and then uses Strategy.deposit() to manage accumulated funds
 * - On withdrawal, if Vault balance is insufficient, it calls Strategy.withdraw() to secure funds
 * - AI Agent (or Vault Master) can change Strategy using setStrategy()
 * - Can be created/deployed by AgentVaultFactory
 */
contract AgentVault is IAgentVault, ERC4626, AccessControl, ReentrancyGuard {
    bytes32 public constant VAULT_MASTER_ROLE = keccak256("VAULT_MASTER_ROLE");
    bytes32 public constant AGENT_ROLE = keccak256("AGENT_ROLE");

    /// @inheritdoc IAgentVault
    IStrategy public override strategy;

    /**
     * @notice Constructor for AgentVault
     * @param asset_ ERC20 asset address for deposits/withdrawals
     * @param _strategy Initial Strategy contract address (its want() must match asset_)
     * @param vaultMaster Vault Master address
     * @param agent AI Agent address
     * @param name Vault token name
     * @param symbol Vault token symbol
     */
    constructor(
        IERC20 asset_,
        IStrategy _strategy,
        address vaultMaster,
        address agent,
        string memory name,
        string memory symbol
    ) ERC20(name, symbol) ERC4626(asset_) {
        require(address(asset_) == address(_strategy.want()), "Vault: asset mismatch with strategy");
        strategy = _strategy;
        // Pre-approve strategy to use vault's assets (maximum amount)
        IERC20(asset_).approve(address(strategy), type(uint256).max);

        // Set roles
        _grantRole(VAULT_MASTER_ROLE, vaultMaster);
        _setRoleAdmin(AGENT_ROLE, VAULT_MASTER_ROLE);
        if (agent != address(0)) {
            _grantRole(AGENT_ROLE, agent);
        }
    }

    /// @inheritdoc IAgentVault
    function totalAssets() public view override(ERC4626, IAgentVault) returns (uint256) {
        return IERC20(asset()).balanceOf(address(this)) + strategy.balanceOf();
    }

    /// @inheritdoc IAgentVault
    function earn() public {
        uint256 available = IERC20(asset()).balanceOf(address(this));
        if (available > 0) {
            IERC20(asset()).transfer(address(strategy), available);
            strategy.deposit();
        }
    }

    /// @inheritdoc IAgentVault
    function deposit(
        uint256 assets,
        address receiver
    ) public override(ERC4626, IAgentVault) nonReentrant returns (uint256 shares) {
        // Pre-work related to strategy (e.g., harvest)
        strategy.beforeDeposit();
        shares = super.deposit(assets, receiver);
        earn();
        return shares;
    }

    /// @inheritdoc IAgentVault
    function redeem(
        uint256 shares,
        address receiver,
        address owner
    ) public override(ERC4626, IAgentVault) nonReentrant returns (uint256 assets) {
        // ì¸ì¶œí•  ìžì‚° ìˆ˜ëŸ‰ ë¯¸ë¦¬ ê³„ì‚°
        uint256 assetsToWithdraw = previewRedeem(shares);

        // Vault ìž”ì•¡ í™•ì¸
        uint256 currentBalance = IERC20(asset()).balanceOf(address(this));

        // ìž”ì•¡ì´ ë¶€ì¡±í•˜ë©´ Strategyì—ì„œ ìžì‚° ì¸ì¶œ
        if (currentBalance < assetsToWithdraw) {
            uint256 needed = assetsToWithdraw - currentBalance;
            strategy.withdraw(needed);
        }

        // ìƒìœ„ í´ëž˜ìŠ¤ redeem í˜¸ì¶œ
        assets = super.redeem(shares, receiver, owner);
        return assets;
    }

    /// @inheritdoc IAgentVault
    function depositAll() external returns (uint256 shares) {
        uint256 balance = IERC20(asset()).balanceOf(msg.sender);
        return deposit(balance, msg.sender);
    }

    /// @inheritdoc IAgentVault
    function redeemAll() external returns (uint256 shares) {
        return redeem(balanceOf(msg.sender), msg.sender, msg.sender);
    }

    /// @inheritdoc IAgentVault
    function setStrategy(IStrategy newStrategy) external {
        require(hasRole(VAULT_MASTER_ROLE, msg.sender) || hasRole(AGENT_ROLE, msg.sender), "Caller is not authorized");
        require(address(asset()) == address(newStrategy.want()), "Vault: new strategy asset mismatch");

        strategy.retireStrategy();
        strategy = newStrategy;
        // Pre-approve new strategy to use vault's assets
        IERC20(asset()).approve(address(newStrategy), type(uint256).max);
        earn();
        emit StrategyChanged(address(newStrategy));
    }

    /// @inheritdoc IAgentVault
    function grantAgentRole(address agent) external {
        require(hasRole(VAULT_MASTER_ROLE, msg.sender), "Caller is not a vault master");
        grantRole(AGENT_ROLE, agent);
    }

    /// @inheritdoc IAgentVault
    function revokeAgentRole(address agent) external {
        require(hasRole(VAULT_MASTER_ROLE, msg.sender), "Caller is not a vault master");
        revokeRole(AGENT_ROLE, agent);
    }
}

contracts/AgentVaultFactory.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.27;

import "@openzeppelin/contracts-upgradeable/access/OwnableUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/proxy/utils/Initializable.sol";
import "@openzeppelin/contracts-upgradeable/proxy/utils/UUPSUpgradeable.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "./AgentVault.sol";
import "./interface/IAgentVaultFactory.sol";

/**
 * @title AgentVaultFactory
 * @notice Factory contract for deploying and managing AgentVault contracts
 */
contract AgentVaultFactory is IAgentVaultFactory, Initializable, OwnableUpgradeable, UUPSUpgradeable {
    /// @notice Array to store all deployed vault addresses
    address[] public vaults;
    /// @notice Initial fund for agent
    uint256 public agentInitialFund;

    /// @custom:oz-upgrades-unsafe-allow constructor
    constructor() {
        _disableInitializers();
    }

    function initialize(uint256 _agentInitialFund) public initializer {
        __Ownable_init(msg.sender);
        __UUPSUpgradeable_init();
        agentInitialFund = _agentInitialFund;
    }

    /// @inheritdoc IAgentVaultFactory
    function createVault(
        IERC20 asset,
        IStrategy strategy,
        address vaultMaster,
        address agent,
        string memory name,
        string memory symbol
    ) external payable returns (address vault) {
        require(address(asset) != address(0), "ERC20: invalid contract address");
        require(vaultMaster != address(0), "Vault: invalid vault master");
        require(msg.value >= agentInitialFund, "Vault: insufficient initial fund");

        vault = address(new AgentVault(asset, strategy, vaultMaster, agent, name, symbol));
        vaults.push(vault);

        (bool success, ) = payable(agent).call{value: agentInitialFund}("");
        require(success, "Vault: failed to transfer initial fund to agent");

        emit VaultCreated(vault, address(asset), name, symbol);
    }

    /// @inheritdoc IAgentVaultFactory
    function getAllVaults() external view returns (address[] memory) {
        return vaults;
    }

    /// @inheritdoc IAgentVaultFactory
    function getVaultCount() external view returns (uint256) {
        return vaults.length;
    }

    function _authorizeUpgrade(address newImplementation) internal override onlyOwner {}
}

contracts/interface/IAgentVault.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/interfaces/IERC4626.sol";
import "./IStrategy.sol";

/**
 * @title IAgentVault
 * @notice Interface for the AgentVault contract that follows ERC-4626 standard
 */
interface IAgentVault is IERC4626 {
    /**
     * @notice Event emitted when Strategy is changed
     */
    event StrategyChanged(address indexed newStrategy);

    /**
     * @notice Current Strategy contract connected to the Vault
     */
    function strategy() external view returns (IStrategy);

    /**
     * @notice Total assets = Vault balance + Assets deposited in Strategy
     */
    function totalAssets() external view returns (uint256);

    /**
     * @notice Deposits all assets in Vault to Strategy for management
     */
    function earn() external;

    /**
     * @notice User deposit function (Override of ERC4626's deposit)
     * - Calls Strategy.beforeDeposit() before deposit
     * - After deposit, calls earn() to manage Vault balance through Strategy
     * @param assets Amount of assets to deposit
     * @param receiver Address to receive Vault tokens
     * @return shares Number of Vault tokens minted (ERC4626 standard)
     */
    function deposit(uint256 assets, address receiver) external returns (uint256 shares);

    /**
     * @notice User redemption function (Override of ERC4626's redeem)
     * - If Vault balance is insufficient, withdraws funds from Strategy
     * @param shares Amount of shares to redeem
     * @param receiver Address to receive assets
     * @param owner Address of Vault token owner
     * @return assets Number of assets returned to receiver (ERC4626 standard)
     */
    function redeem(uint256 shares, address receiver, address owner) external returns (uint256 assets);

    /**
     * @notice Convenience function: Deposits all assets of msg.sender to Vault
     */
    function depositAll() external returns (uint256 shares);

    /**
     * @notice Convenience function: Withdraws all assets using msg.sender's Vault tokens
     */
    function redeemAll() external returns (uint256 shares);

    /**
     * @notice Function for AI Agent or Vault Master to change Strategy
     * - Calls retireStrategy() on existing Strategy to recover funds
     * - New Strategy's want() must match current Vault's asset
     * - Calls earn() after change to deposit Vault balance to new Strategy
     * @param newStrategy Address of new Strategy contract
     */
    function setStrategy(IStrategy newStrategy) external;

    /**
     * @notice Function for Vault Master to grant AI Agent role
     */
    function grantAgentRole(address agent) external;

    /**
     * @notice Function for Vault Master to revoke AI Agent role
     */
    function revokeAgentRole(address agent) external;
}

contracts/interface/IAgentVaultFactory.s.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.27;

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "./IStrategy.sol";

/**
 * @title IAgentVaultFactory
 * @notice Interface for the VaultFactory contract that manages vault creation and administration
 */
interface IAgentVaultFactory {
    /**
     * @notice Event emitted when a new vault is created
     */
    event VaultCreated(address indexed vault, address indexed asset, string name, string symbol);

    /**
     * @notice Creates a new AgentVault.
     * @param asset The ERC20 asset address for deposit/withdraw operations.
     * @param strategy The strategy contract address that AgentVault will use.
     * @param vaultMaster The address that will have the VAULT_MASTER_ROLE in the new vault.
     * @param agent The address that will have the AGENT_ROLE in the new vault.
     * @param name The name of the vault token.
     * @param symbol The symbol of the vault token.
     * @return vault The address of the newly deployed AgentVault contract.
     */
    function createVault(
        IERC20 asset,
        IStrategy strategy,
        address vaultMaster,
        address agent,
        string memory name,
        string memory symbol
    ) external payable returns (address vault);

    /**
     * @notice Returns all deployed vault addresses
     * @return Array of vault addresses
     */
    function getAllVaults() external view returns (address[] memory);

    /**
     * @notice Returns the total number of deployed vaults
     * @return Number of vaults
     */
    function getVaultCount() external view returns (uint256);
}

contracts/interface/IStrategy.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

import {IERC20} from "@openzeppelin/contracts/token/ERC20/IERC20.sol";

/**
 * @title IStrategy
 * @notice Interface that defines the functions that Strategy contracts must implement to interact with the Vault
 */
interface IStrategy {
    /// @notice Returns the address of the asset (ERC20) managed by the Vault
    function want() external view returns (IERC20);

    /// @notice Returns the total amount of assets currently managed by the Strategy (including deposits and interest)
    function balanceOf() external view returns (uint256);

    /// @notice Called when funds are deposited from the Vault - implements logic to deposit funds into the actual DeFi protocol
    function deposit() external;

    /// @notice Called when withdrawal is requested from the Vault - implements logic to return funds to the Vault
    function withdraw(uint256 amount) external;

    /// @notice Called before Vault deposits - performs necessary preliminary work (e.g., harvest)
    function beforeDeposit() external;

    /// @notice Function to withdraw funds from the current Strategy when switching strategies
    function retireStrategy() external;
}

