     * @param _stakingToken ìŠ¤í…Œì´í‚¹í•  í† í° ì£¼ì†Œ
     * @param _yieldRate ì—°ê°„ ìˆ˜ìµë¥  (ì˜ˆ: 1000 = 10%)
     */
    constructor(IERC20 _stakingToken, uint256 _yieldRate) {
        stakingToken = _stakingToken;
        yieldRate = _yieldRate;
    }

    /**
     * @notice ìˆ˜ìµë¥  ì„¤ì •
     * @param _yieldRate ìƒˆë¡œìš´ ìˆ˜ìµë¥  (ì˜ˆ: 1000 = 10%)
     */
    function setYieldRate(uint256 _yieldRate) external {
        yieldRate = _yieldRate;
    }

    /**
     * @notice í† í° ìŠ¤í…Œì´í‚¹ í•¨ìˆ˜
     * @param amount ìŠ¤í…Œì´í‚¹í•  í† í° ìˆ˜ëŸ‰
     */
    function stake(uint256 amount) external {
        require(amount > 0, "Cannot stake 0");

        // ë¨¼ì € ê¸°ì¡´ ìˆ˜ìµ ê³„ì‚°
        _calculateYield(msg.sender);

        // í† í° ì „ì†¡
        stakingToken.transferFrom(msg.sender, address(this), amount);

        // ìŠ¤í…Œì´í‚¹ ìž”ì•¡ ì—…ë°ì´íŠ¸
        _stakedBalance[msg.sender] += amount;
        _totalStaked += amount;

        // ë§ˆì§€ë§‰ ìˆ˜ìµ ê³„ì‚° ì‹œê°„ ì—…ë°ì´íŠ¸
        lastYieldTime[msg.sender] = block.timestamp;
    }

    /**
     * @notice ìŠ¤í…Œì´í‚¹ëœ í† í° ì¶œê¸ˆ í•¨ìˆ˜
     * @param amount ì¶œê¸ˆí•  í† í° ìˆ˜ëŸ‰
     */
    function unstake(uint256 amount) external {
        require(amount > 0, "Cannot unstake 0");
        require(_stakedBalance[msg.sender] >= amount, "Insufficient staked balance");

        // ë¨¼ì € ê¸°ì¡´ ìˆ˜ìµ ê³„ì‚°
        _calculateYield(msg.sender);

        // ìŠ¤í…Œì´í‚¹ ìž”ì•¡ ì—…ë°ì´íŠ¸
        _stakedBalance[msg.sender] -= amount;
        _totalStaked -= amount;

        // í† í° ì „ì†¡
        stakingToken.transfer(msg.sender, amount);

        // ë§ˆì§€ë§‰ ìˆ˜ìµ ê³„ì‚° ì‹œê°„ ì—…ë°ì´íŠ¸
        lastYieldTime[msg.sender] = block.timestamp;
    }

    /**
     * @notice ë°œìƒí•œ ìˆ˜ìµ ê³„ì‚° ë° ì§€ê¸‰
     */
    function harvest() external {
        _calculateYield(msg.sender);
    }

    /**
     * @notice ì‚¬ìš©ìžì˜ ìŠ¤í…Œì´í‚¹ëœ ìž”ì•¡ ì¡°íšŒ
     * @param account ì¡°íšŒí•  ì‚¬ìš©ìž ì£¼ì†Œ
     * @return ìŠ¤í…Œì´í‚¹ëœ ìž”ì•¡ (ìˆ˜ìµ í¬í•¨)
     */
    function stakedBalanceOf(address account) external view returns (uint256) {
        if (_stakedBalance[account] == 0) {
            return 0;
        }

        uint256 timeElapsed = block.timestamp - lastYieldTime[account];
        uint256 yield = (_stakedBalance[account] * yieldRate * timeElapsed) / (365 days * 10000);

        return _stakedBalance[account] + yield;
    }

    /**
     * @notice ì´ ìŠ¤í…Œì´í‚¹ëœ ê¸ˆì•¡ ì¡°íšŒ
     * @return ì´ ìŠ¤í…Œì´í‚¹ëœ ê¸ˆì•¡
     */
    function totalStaked() external view returns (uint256) {
        return _totalStaked;
    }

    /**
     * @notice ë‚´ë¶€ í•¨ìˆ˜: ì‚¬ìš©ìžì˜ ìˆ˜ìµ ê³„ì‚° ë° ì§€ê¸‰
     * @param account ìˆ˜ìµì„ ê³„ì‚°í•  ì‚¬ìš©ìž ì£¼ì†Œ
     */
    function _calculateYield(address account) internal {
        if (_stakedBalance[account] == 0 || lastYieldTime[account] == 0) {
            lastYieldTime[account] = block.timestamp;
            return;
        }

        uint256 timeElapsed = block.timestamp - lastYieldTime[account];
        if (timeElapsed == 0) return;

        uint256 yield = (_stakedBalance[account] * yieldRate * timeElapsed) / (365 days * 10000);

        if (yield > 0) {
            // í…ŒìŠ¤íŠ¸ í† í° ë°œí–‰í•˜ì—¬ ìˆ˜ìµìœ¼ë¡œ ì§€ê¸‰
            TestToken(address(stakingToken)).mint(address(this), yield);
            _stakedBalance[account] += yield;
            _totalStaked += yield;
        }

        lastYieldTime[account] = block.timestamp;
    }
}

contracts/mock/TestStrategy.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.27;

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "contracts/interface/IStrategy.sol";
import "./TestDefi.sol";

/**
 * @title TestStrategy
 * @notice í…ŒìŠ¤íŠ¸ìš© Strategy ì»¨íŠ¸ëž™íŠ¸
 * - TestDefi í”„ë¡œí† ì½œê³¼ ìƒí˜¸ìž‘ìš©í•˜ëŠ” ì „ëžµ ì»¨íŠ¸ëž™íŠ¸
 * - AgentVaultì™€ TestDefi ì‚¬ì´ì˜ ì¤‘ê°œ ì—­í•  ìˆ˜í–‰
 */
contract TestStrategy is IStrategy {
    /// @notice ê´€ë¦¬í•˜ëŠ” ìžì‚° í† í°
    IERC20 public immutable want;

    /// @notice ìƒí˜¸ìž‘ìš©í•  TestDefi ì»¨íŠ¸ëž™íŠ¸ ì¸ìŠ¤í„´ìŠ¤
    TestDefi public immutable defi;

    /**
     * @notice ìƒì„±ìž
     * @param _want ê´€ë¦¬í•  ìžì‚° í† í° ì£¼ì†Œ
     * @param _defi TestDefi ì»¨íŠ¸ëž™íŠ¸ ì£¼ì†Œ
     */
    constructor(IERC20 _want, TestDefi _defi) {
        want = _want;
        defi = _defi;

        // TestDefiì— ìžì‚° í† í° ì‚¬ìš© ê¶Œí•œ ë¶€ì—¬
        want.approve(address(_defi), type(uint256).max);
    }

    /**
     * @notice TestDefiì˜ ìˆ˜ìµë¥  ì„¤ì • (í•„ìš”í•œ ê²½ìš° ì‚¬ìš©)
     * @param _yieldRate ìƒˆë¡œìš´ ìˆ˜ìµë¥  (ì˜ˆ: 1000 = 10%)
     */
    function setYieldRate(uint256 _yieldRate) external {
        defi.setYieldRate(_yieldRate);
    }

    /// @inheritdoc IStrategy
    function deposit() external override {
        uint256 balance = want.balanceOf(address(this));
        if (balance > 0) {
            defi.stake(balance);
        }
    }

    /// @inheritdoc IStrategy
    function withdraw(uint256 amount) external override {
        // í˜„ìž¬ TestDefiì— ìŠ¤í…Œì´í‚¹ëœ ìž”ì•¡ í™•ì¸
        uint256 stakedBalance = defi.stakedBalanceOf(address(this));
        require(amount <= stakedBalance, "Strategy: insufficient staked amount");

        // TestDefiì—ì„œ ìžì‚° ì¶œê¸ˆ
        defi.unstake(amount);

        // ìš”ì²­ìž(AgentVault)ì—ê²Œ ìžì‚° ì „ì†¡
        want.transfer(msg.sender, amount);
    }

    /// @inheritdoc IStrategy
    function beforeDeposit() external override {
        // TestDefiì—ì„œ ìˆ˜ìµ ìˆ˜í™•
        defi.harvest();
    }

    /// @inheritdoc IStrategy
    function retireStrategy() external override {
        // TestDefiì—ì„œ ëª¨ë“  ìžì‚° ì¶œê¸ˆ
        uint256 stakedBalance = defi.stakedBalanceOf(address(this));
        if (stakedBalance > 0) {
            defi.unstake(stakedBalance);
        }

        // ëª¨ë“  ìžì‚°ì„ Vaultë¡œ ë°˜í™˜
        uint256 balance = want.balanceOf(address(this));
        if (balance > 0) {
            want.transfer(msg.sender, balance);
        }
    }

    /// @inheritdoc IStrategy
    function balanceOf() external view override returns (uint256) {
        // TestDefiì— ìŠ¤í…Œì´í‚¹ëœ ìžì‚° + ì´ ì»¨íŠ¸ëž™íŠ¸ê°€ ë³´ìœ í•œ ìžì‚°
        return defi.stakedBalanceOf(address(this)) + want.balanceOf(address(this));
    }
}

